mapper.select:
SELECT A.BNR_ID , A.BNR_TITLE , A.EXPO_BGN_DTTM , A.EXPO_END_DTTM , A.USE_YN , (SELECT CD_NM FROM COM_CODE_SPEC WHERE CD = 'P_ROW_POS' AND CD_SPEC = A.PAR_ROW_POS) PAR_ROW_POS , (SELECT CD_NM FROM COM_CODE_SPEC WHERE CD = 'ROW_POS' AND CD_SPEC = A.ROW_POS) ROW_POS , A.DEL_YN , A.REGR_ID , A.UPDR_ID , A.RNUM SEQ FROM ( SELECT S.BNR_ID , S.BNR_TITLE , S.EXPO_BGN_DTTM , S.EXPO_END_DTTM , S.USE_YN , S.PAR_ROW_POS , S.ROW_POS , S.DEL_YN , S.REGR_ID , S.UPDR_ID , ROWNUM RNUM FROM ( SELECT A.BNR_ID , A.BNR_TITLE , TO_CHAR(A.EXPO_BGN_DTTM, 'YYYY-MM-DD') EXPO_BGN_DTTM , TO_CHAR(A.EXPO_END_DTTM, 'YYYY-MM-DD') EXPO_END_DTTM , (CASE WHEN SYSDATE BETWEEN A.EXPO_BGN_DTTM AND A.EXPO_END_DTTM THEN 'Y' ELSE 'N' END) USE_YN , A.ROW_POS , A.PAR_ROW_POS , A.DEL_YN , A.REGR_ID , A.UPDR_ID FROM ADM_BANNER_INFO A WHERE DEL_YN = 'N'
AND A.BNR_TITLE LIKE '%'|| #{searchKeyword} || '%'
ORDER BY A.BNR_ID DESC ) S ) A WHERE ROWNUM <= #{recordCountPerPage} and RNUM > #{firstIndex}

mapper.insert:
	
		SELECT (LPAD(TO_CHAR((SELECT NVL(MAX(TO_NUMBER(APND_FILE_SEQ)),0)+1 FROM ADM_BANNER_APND_FILE)), '6', '0')) AS APND_FILE_SEQ
