		, REGR_ID
		, REG_DTTM
		, UPDR_ID
		, UPD_DTTM
		) VALUES (
		 #{bnrId}
		, #{apndFileSeq}
		, #{apndFileSz}
		, #{apndFileOrgn}
		, #{apndFileName}
		, #{apndFilePath}
		, #{delYn}
