mapper.delete:
/* === AdmMainMapper.deleteStandardContent === */
	DELETE FROM COM_STND_CNTS_INFO
	 WHERE CNTS_TP = #{cntsTp}

/* === AdmMainMapper.deleteStandardBoard === */
	DELETE FROM COM_STND_BOARD_INFO
	 WHERE BOARD_USE_DIV = #{boardUseDiv}

mapper.insert:
/* === AdmMainMapper.insertStandardBoard === */
	 INSERT INTO COM_STND_BOARD_INFO 
	 (
	    BOARD_USE_DIV
	  , STND_BOARD_SEQ
	  , MNDT_YN
	  , BOARD_ID
	  , BOARD_NAME
	  , BOARD_URL
	  , SORT_ORD
	  , DEL_YN
	  , REGR_ID
	  , REGR_NAME
	  , REG_DTTM
	  , UPDR_ID
	  , UPDR_NAME
	  , UPD_DTTM
	  , OLD_TABLE
	  , OLD_USE
	  , OLD_SEQ
	 ) 
	 VALUES
	 ( 
 	    #{boardUseDiv}
 	  , #{stndBoardSeq}
 	  , #{mndtYn}
 	  , #{boardId}
      , #{boardName}
      , ''
      , #{stndBoardSeq}
      , 'N'
      , #{userId}
      , #{userName}
      , SYSDATE
      , #{userId}
      , #{userName}
      , SYSDATE 
      , ''
      , ''
      , ''
	 )

mapper.update:
/* === AdmMainMapper.updateWorkDesk === */	
	UPDATE ADM_BIZ_LINK
	   SET MAIN_DSP_GB = 'Y'
	     , UPD_DTTM = SYSDATE
	     , UPDR_ID = #{userId}
	 WHERE LINK_CAT_ID = #{linkCatId}
	   AND LINK_CD = #{linkCd}
