mapper.select:
SELECT A.POP_ID , A.POP_TITLE , A.EXPO_BGN_DTTM , A.EXPO_END_DTTM , A.USE_YN , (SELECT CD_NM FROM COM_CODE_SPEC WHERE CD = 'P_ROW_POS' AND CD_SPEC = A.PAR_ROW_POS) PAR_ROW_POS , (SELECT CD_NM FROM COM_CODE_SPEC WHERE CD = 'ROW_POS' AND CD_SPEC = A.ROW_POS) ROW_POS , A.POP_KIND , A.EXPO_YN , A.LINK_URL , A.LINK_KIND , A.DEL_YN , A.REGR_ID , A.UPDR_ID , A.RNUM SEQ FROM ( SELECT S.POP_ID , S.POP_TITLE , S.EXPO_BGN_DTTM , S.EXPO_END_DTTM , S.USE_YN , S.PAR_ROW_POS , S.POP_KIND , S.EXPO_YN , S.LINK_URL , S.LINK_KIND , S.ROW_POS , S.DEL_YN , S.REGR_ID , S.UPDR_ID , ROWNUM RNUM FROM ( SELECT A.POP_ID , A.POP_TITLE , TO_CHAR(A.EXPO_BGN_DTTM, 'YYYY-MM-DD') EXPO_BGN_DTTM , TO_CHAR(A.EXPO_END_DTTM, 'YYYY-MM-DD') EXPO_END_DTTM , (CASE WHEN SYSDATE BETWEEN A.EXPO_BGN_DTTM AND A.EXPO_END_DTTM THEN 'Y' ELSE 'N' END) USE_YN , A.ROW_POS , A.PAR_ROW_POS , A.POP_KIND , A.EXPO_YN , A.LINK_URL , A.LINK_KIND , A.DEL_YN , A.REGR_ID , A.UPDR_ID FROM ADM_POP_INFO A WHERE DEL_YN = 'N'
AND A.POP_TITLE LIKE '%'|| #{searchKeyword} || '%'
ORDER BY A.POP_ID DESC ) S ) A WHERE ROWNUM <= #{recordCountPerPage} and RNUM > #{firstIndex}

mapper.insert:
	
		SELECT	LPAD( NVL(MAX(TO_NUMBER(POP_ID)) ,0)+1 , '6', '0') POP_ID
		FROM	ADM_POP_INFO   
    
    	
	INSERT INTO ADM_POP_INFO (                                                                                                       
	    POP_ID																	
		,POP_TITLE																		
		,EXPO_BGN_DTTM																	
		,EXPO_END_DTTM
		,PAR_ROW_POS
		,ROW_POS
		, POP_KIND
		, EXPO_YN
		, LINK_URL
		, LINK_KIND			
		, POP_CONTS			
		,DEL_YN
		,REGR_ID
		,REG_DTTM
		,UPDR_ID 
		,UPD_DTTM                                                                        
	) VALUES (
	    #{popId}																	
		,#{popTitle}																		
		,TO_DATE(#{expoBgnDttm, jdbcType=VARCHAR}, 'YYYYMMDDHH24MISS')																	
		,TO_DATE(#{expoEndDttm, jdbcType=VARCHAR}, 'YYYYMMDDHH24MISS')
		,#{parRowPos}
		,#{rowPos}
		, #{popKind}
		, #{expoYn}
		, #{linkUrl}
		, #{linkKind}				
		, #{popConts}				
		,#{delYn}
		,#{regrId}
		,SYSDATE
		,#{updrId} 
		,SYSDATE                                                     
	)

mapper.update:
		UPDATE ADM_POP_INFO         
		SET
			POP_TITLE = #{popTitle}
			, EXPO_BGN_DTTM = TO_DATE(#{expoBgnDttm, jdbcType=VARCHAR}, 'YYYYMMDDHH24MISS')
			, EXPO_END_DTTM = TO_DATE(#{expoEndDttm, jdbcType=VARCHAR}, 'YYYYMMDDHH24MISS')
			, PAR_ROW_POS = #{parRowPos}                                                              
			, ROW_POS = #{rowPos}  
			, POP_KIND = #{popKind}
			, EXPO_YN = #{expoYn}
			, LINK_URL = #{linkUrl}
			, LINK_KIND = #{linkKind}				                                                            
			, POP_CONTS = #{popConts}				                                                            
			, UPDR_ID = #{updrId}
			, UPD_DTTM = SYSDATE 
 		WHERE POP_ID = #{popId}

