SELECT TR.TOT_COUNT TOT_CONTENT_CNT ,TR.REPL_COUNT RE_CNT ,CNT.READ_CNT VIEW_CNT ,CNT.OPINION_CNT OPINION_CNT ,CNT.APPROVAL_CNT APPROVAL_CNT ,CNT.OPPOSITION_CNT OPPOSITION_CNT ,CNT.LIKE_CNT LIKE_CNT ,DM.MANAGER_DEL ADM_DEL_CNT ,DM.SELF_DEL DEL_CNT ,DM.MANAGER_MOVE ADM_MOVE_CNT ,DM.SELF_MOVE MOVE_CNT ,EC.IMAGE_CNT ,EC.MOVIE_CNT ,ES.SURVEY_CNT
,'' INS_DTS
,( SELECT TO_CHAR(MAX(REG_DTTM) ,'YYYYMMDD') INS_DTS FROM BBS_BOARD_INFO WHERE 1=1 AND BOARD_ID IN
#{item}
) INS_DTS
FROM ( SELECT NVL(SUM(TOT_COUNT) ,0) TOT_COUNT ,NVL(SUM(REPL_COUNT) ,0) REPL_COUNT FROM ( SELECT DECODE(MAX(UP_NOTI_ID),'*' ,COUNT(UP_NOTI_ID) ,0) TOT_COUNT ,DECODE(MAX(UP_NOTI_ID),'*' ,0 ,COUNT(UP_NOTI_ID)) REPL_COUNT FROM BBS_NOTI_INFO WHERE 1=1 AND NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND BOARD_ID IN
#{item}
GROUP BY UP_NOTI_ID ) ) TR ,( SELECT NVL(SUM(MANAGER_DEL) ,0) MANAGER_DEL ,NVL(SUM(SELF_DEL) ,0) SELF_DEL ,NVL(SUM(MANAGER_MOVE) ,0) MANAGER_MOVE ,NVL(SUM(SELF_MOVE) ,0) SELF_MOVE FROM ( SELECT DECODE(MAX(STAT_CODE), '1', COUNT(STAT_CODE), 0) MANAGER_DEL ,DECODE(MAX(STAT_CODE), '2', COUNT(STAT_CODE), 0) SELF_DEL ,DECODE(MAX(STAT_CODE), '3', COUNT(STAT_CODE), 0) MANAGER_MOVE ,DECODE(MAX(STAT_CODE), '4', COUNT(STAT_CODE), 0) SELF_MOVE FROM BBS_NOTI_INFO WHERE 1=1 AND NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND BOARD_ID IN
#{item}
GROUP BY STAT_CODE ) ) DM ,( SELECT NVL(SUM(NOTI_READ_CNT) ,0) READ_CNT ,NVL(SUM(NOTI_OPN_CNT) ,0) OPINION_CNT ,NVL(SUM(NOTI_AGRM_CNT) ,0) APPROVAL_CNT ,NVL(SUM(NOTI_OPP_CNT) ,0) OPPOSITION_CNT ,NVL(SUM(NOTI_LIKE_CNT) ,0) LIKE_CNT FROM BBS_NOTI_INFO WHERE 1=1 AND NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND BOARD_ID IN
#{item}
) CNT ,( SELECT NVL(SUM(DECODE(APND_FILE_TP ,'020' ,1 ,0)) ,0) IMAGE_CNT ,NVL(SUM(DECODE(APND_FILE_TP ,'030' ,1 ,0)) ,0) MOVIE_CNT FROM BBS_NOTI_APND_FILE WHERE 1=1 AND NOTI_ID IN ( SELECT DISTINCT NOTI_ID FROM BBS_NOTI_INFO WHERE NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND BOARD_ID IN
#{item}
) ) EC ,( SELECT NVL(SUM(COUNT(NOTI_ID)) ,0) SURVEY_CNT FROM BBS_NOTI_SURVEY WHERE NOTI_ID IN ( SELECT DISTINCT NOTI_ID FROM BBS_NOTI_INFO WHERE NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND BOARD_ID IN
#{item}
) GROUP BY NOTI_ID ) ES

SELECT TR.TOT_COUNT TOT_CONTENT_CNT ,TR.REPL_COUNT RE_CNT ,CNT.READ_CNT VIEW_CNT ,CNT.OPINION_CNT OPINION_CNT ,CNT.APPROVAL_CNT APPROVAL_CNT ,CNT.OPPOSITION_CNT OPPOSITION_CNT ,CNT.LIKE_CNT LIKE_CNT ,DM.MANAGER_DEL ADM_DEL_CNT ,DM.SELF_DEL DEL_CNT ,DM.MANAGER_MOVE ADM_MOVE_CNT ,DM.SELF_MOVE MOVE_CNT ,EC.IMAGE_CNT ,EC.MOVIE_CNT ,0 SURVEY_CNT
,'' INS_DTS
,( SELECT TO_CHAR(MAX(REG_DTTM) ,'YYYYMMDD') INS_DTS FROM PBS_USER_BOARD_INFO WHERE 1=1 AND BOARD_ID = #{brdId} ) INS_DTS
FROM ( SELECT NVL(TOT_COUNT ,0) TOT_COUNT ,NVL(REPL_COUNT ,0) REPL_COUNT FROM (
SELECT SUM(CASE WHEN UP_USER_NOTI_SEQ=0 AND USER_NOTI_SEQ<>0 THEN 1 ELSE 0 END) TOT_COUNT ,SUM(CASE WHEN UP_USER_NOTI_SEQ<>0 THEN 1 ELSE 0 END) REPL_COUNT
FROM PBS_USER_NOTI_INFO WHERE 1=1 AND NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND DEPT_CODE = #{deptCode}
AND BOARD_ID = #{brdId}
AND BOARD_ID IN ( SELECT BOARD_ID FROM PBS_USER_BOARD_INFO WHERE 1=1 AND BOARD_OWNR_ID = #{ownrId} )
) ) TR ,( SELECT NVL(SUM(MANAGER_DEL) ,0) MANAGER_DEL ,NVL(SUM(SELF_DEL) ,0) SELF_DEL ,NVL(SUM(MANAGER_MOVE) ,0) MANAGER_MOVE ,NVL(SUM(SELF_MOVE) ,0) SELF_MOVE FROM ( SELECT DECODE(MAX(STAT_CODE), '1', COUNT(STAT_CODE), 0) MANAGER_DEL ,DECODE(MAX(STAT_CODE), '2', COUNT(STAT_CODE), 0) SELF_DEL ,DECODE(MAX(STAT_CODE), '3', COUNT(STAT_CODE), 0) MANAGER_MOVE ,DECODE(MAX(STAT_CODE), '4', COUNT(STAT_CODE), 0) SELF_MOVE FROM PBS_USER_NOTI_INFO WHERE 1=1 AND NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND DEPT_CODE = #{deptCode}
AND BOARD_ID = #{brdId}
AND BOARD_ID IN ( SELECT BOARD_ID FROM PBS_USER_BOARD_INFO WHERE 1=1 AND BOARD_OWNR_ID = #{ownrId} )
GROUP BY STAT_CODE ) ) DM ,( SELECT NVL(SUM(NOTI_READ_CNT) ,0) READ_CNT ,NVL(SUM(NOTI_OPN_CNT) ,0) OPINION_CNT ,NVL(SUM(NOTI_AGRM_CNT) ,0) APPROVAL_CNT ,NVL(SUM(NOTI_OPP_CNT) ,0) OPPOSITION_CNT ,NVL(SUM(NOTI_LIKE_CNT) ,0) LIKE_CNT FROM PBS_USER_NOTI_INFO WHERE 1=1 AND NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND DEPT_CODE = #{deptCode}
AND BOARD_ID = #{brdId}
AND BOARD_ID IN ( SELECT BOARD_ID FROM PBS_USER_BOARD_INFO WHERE 1=1 AND BOARD_OWNR_ID = #{ownrId} )
) CNT ,( SELECT NVL(SUM(DECODE(APND_FILE_TP ,'020' ,1 ,0)) ,0) IMAGE_CNT ,NVL(SUM(DECODE(APND_FILE_TP ,'030' ,1 ,0)) ,0) MOVIE_CNT FROM PBS_USER_NOTI_APND_FILE WHERE 1=1 AND USER_NOTI_SEQ IN ( SELECT DISTINCT USER_NOTI_SEQ FROM PBS_USER_NOTI_INFO WHERE NOTI_BGN_DTTM BETWEEN TO_DATE(#{sFromDt} ,'YYYYMMDD') AND TO_DATE(#{sToDt} ,'YYYYMMDD')
AND DEPT_CODE = #{deptCode}
AND BOARD_ID = #{brdId}
AND BOARD_ID IN ( SELECT BOARD_ID FROM PBS_USER_BOARD_INFO WHERE 1=1 AND BOARD_OWNR_ID = #{ownrId} )
) ) EC

SELECT BOARD_ID ,INS_NAME ,BOARD_TITLE ,DEL_YN ,INS_DTS FROM ( SELECT ROWNUM RNUM ,B.* FROM ( SELECT BOARD_ID ,BOARD_OWNR_NAME INS_NAME ,BOARD_NAME BOARD_TITLE ,DEL_YN ,TO_CHAR(REG_DTTM ,'YYYYMMDD') INS_DTS FROM PBS_USER_BOARD_INFO WHERE 1=1
AND BOARD_ID IN (SELECT BOARD_ID FROM PBS_USER_BOARD_PART_INFO WHERE USER_ID = #{deptCode} AND USER_DIV = 'DPT')
AND BOARD_OWNR_ID = #{ownrId}
AND BOARD_NAME LIKE '%'||#{searchKeyword}||'%'
ORDER BY BOARD_NAME ,REG_DTTM DESC ) B ) P
WHERE RNUM <= #{recordCountPerPage} and RNUM >= #{firstIndex}

SELECT P.RNUM ,P.BOARD_NAME ,P.NOTI_ID ,P.BOARD_ID ,P.NOTI_TITLE ,P.SURVEY_NO ,P.SURVEY_CONTS ,P.SURVEY_TP ,P.REGR_ID ,P.REGR_NAME ,CASE WHEN P.SURVEY_RSLT_OPEN_YN = 'Y' THEN '공개' ELSE '비공개' END SURVEY_RSLT_OPEN_YN ,TO_CHAR(P.SURVEY_CLOS_DTTM, 'YYYY-MM-DD HH24:MI') SURVEY_CLOS_DTTM ,TO_CHAR(P.NOTI_END_DTTM, 'YYYY-MM-DD') NOTI_END_DTTM ,TO_CHAR(P.REG_DTTM, 'YYYY-MM-DD') REG_DTTM ,(SELECT COUNT(A.TMLN_SEQ) FROM BBS_NOTI_SURVEY A, BBS_NOTI_SURVEY_ANSW B WHERE A.SURVEY_NO = B.SURVEY_NO AND A.NOTI_ID = P.NOTI_ID) TOT_CNT FROM( SELECT ROWNUM RNUM, S.* FROM ( SELECT A.BOARD_NAME ,B.NOTI_ID ,B.BOARD_ID ,B.NOTI_TITLE ,C.SURVEY_NO ,C.SURVEY_CONTS ,C.SURVEY_TP ,C.REGR_ID ,C.REGR_NAME ,C.SURVEY_RSLT_OPEN_YN ,C.SURVEY_CLOS_DTTM ,C.REG_DTTM ,B.NOTI_END_DTTM FROM BBS_BOARD_INFO A, BBS_NOTI_INFO B, BBS_NOTI_SURVEY C WHERE A.BOARD_ID = B.BOARD_ID AND B.NOTI_ID = C.NOTI_ID AND B.NOTI_KIND = '040'
AND A.BOARD_NAME LIKE '%'|| #{boardName} ||'%'
AND B.NOTI_TITLE LIKE '%'|| #{notiTitle} ||'%'
AND C.SURVEY_CONTS LIKE '%'|| #{surveyConts} ||'%'
AND C.REGR_NAME LIKE '%'|| #{regrName} ||'%'
AND C.SURVEY_RSLT_OPEN_YN = #{surveyRsltOpenYn}
AND TO_CHAR(C.SURVEY_CLOS_DTTM, 'YYYYMMDD') >= #{surveyClosDttmStart}
AND TO_CHAR(C.SURVEY_CLOS_DTTM, 'YYYYMMDD') <= #{surveyClosDttmEnd}
AND TO_CHAR(B.NOTI_END_DTTM, 'YYYYMMDD') >= #{notiEndDttmStart}
AND TO_CHAR(B.NOTI_END_DTTM, 'YYYYMMDD') <= #{notiEndDttmEnd}
AND TO_CHAR(C.REG_DTTM, 'YYYYMMDD') >= #{regDttmStart}
AND TO_CHAR(C.REG_DTTM, 'YYYYMMDD') <= #{regDttmEnd}
ORDER BY B.SORT_SEQ DESC ) S ) P WHERE ROWNUM <= #{recordCountPerPage} and RNUM > #{firstIndex}

		SELECT COUNT(B.NOTI_ID) TOTCNT
        FROM BBS_BOARD_INFO A, BBS_NOTI_INFO B, BBS_NOTI_SURVEY C
        WHERE A.BOARD_ID = B.BOARD_ID
        AND B.NOTI_ID = C.NOTI_ID
        AND B.NOTI_KIND = '040'
		
              AND A.BOARD_NAME LIKE '%'|| #{boardName} ||'%'
		
		
              AND B.NOTI_TITLE LIKE '%'|| #{notiTitle} ||'%'
		
		
              AND C.SURVEY_CONTS LIKE '%'|| #{surveyConts} ||'%'
		
		
              AND C.REGR_NAME LIKE '%'|| #{regrName} ||'%'
		
		
              AND C.SURVEY_RSLT_OPEN_YN = #{surveyRsltOpenYn}
		
		
              AND TO_CHAR(C.SURVEY_CLOS_DTTM, 'YYYYMMDD') >= #{surveyClosDttmStart}
		
		
              AND TO_CHAR(C.SURVEY_CLOS_DTTM, 'YYYYMMDD') <= #{surveyClosDttmEnd}
		
		
              AND TO_CHAR(B.NOTI_END_DTTM, 'YYYYMMDD') >= #{notiEndDttmStart}
		
		
              AND TO_CHAR(B.NOTI_END_DTTM, 'YYYYMMDD') <= #{notiEndDttmEnd}
		
		
              AND TO_CHAR(C.REG_DTTM, 'YYYYMMDD') >= #{regDttmStart}
		
		
              AND TO_CHAR(C.REG_DTTM, 'YYYYMMDD') <= #{regDttmEnd}
